mapper.select:
SELECT note_id AS referencing_note_id, COUNT(*) AS referencing_note_count FROM noteverso_note_map
WHERE note_id IN

        #{referencingNoteId}

AND creator = #{userId}
GROUP BY note_id;

SELECT linked_note_id AS referenced_note_id, COUNT(*) AS referenced_note_count FROM noteverso_note_map
WHERE linked_note_id IN

        #{referencedNoteId}

AND creator = #{userId}
GROUP BY linked_note_id;
