mapper.select:
SELECT * FROM noteverso_note
WHERE note_id IN

        #{noteId}

AND creator = #{userId} AND is_deleted = #{isDeleted}
ORDER BY added_at DESC

mapper.update:
UPDATE noteverso_note
SET is_archived = #{isArchived}, updated_at = NOW()
WHERE project_id = #{projectId} AND creator=#{userId}

UPDATE noteverso_note
SET is_deleted = 1, updated_at = NOW(), project_id = null
WHERE project_id = #{projectId} AND creator=#{userId}
